    if tokens[0] == 'False' and tokens[2] == 'False':

        return False

    elif tokens[0] == 'False' and tokens[2] == 'True':

        return True

    elif tokens[0] == 'True' and tokens[2] == 'False':

        return False

    elif tokens[0] == 'True' and tokens[2] == 'True':

        return True

    else:

